brain:
// Init memory
if (!context.memory.initialized) {
    context.memory.initialized = true;
    context.memory.evadeTicks = 0;
}

let state = context.state;
let action = context.action;
let closest = state.proximity[0] || {};
let enemy = false;

// 1. Find valid enemy
if (state.scanner && state.scanner.robot) {
    for (let i = 0; i < state.scanner.robot.length; i++) {
        let r = state.scanner.robot[i];
        if (r.active && r.team !== state.team) {
            enemy = r;
            break;
        }
    }
}

// 2. Check for damage
let takingDamage = state.lastDamage && state.lastDamage.length > 0;

if (takingDamage || context.memory.evadeTicks > 0) {
    // EVASIVE MANEUVERS
    if (takingDamage) {
        context.memory.evadeTicks = 15;
        // Turn 90 degrees left or right relative to current angle
        let turn = Math.random() > 0.5 ? Math.PI / 2 : -Math.PI / 2;
        context.memory.evadeAngle = state.angle + turn;
    } else {
        context.memory.evadeTicks--;
    }
    action.desiredAngle = context.memory.evadeAngle;
    action.desiredForce = state.maxForce; // Full speed
    action.desiredLaunch = false;
} 
else if (closest.entityType === "wall") {
    // WALL AVOIDANCE
    action.desiredAngle = state.angleToCenter;
    action.desiredForce = state.maxForce;
    action.desiredLaunch = false;
} 
else if (enemy) {
    // ATTACK MODE
    // Target locked
    action.desiredAngle = enemy.angleFromRobot;
    action.desiredLaunch = true;
    // Move slowly towards them
    action.desiredForce = state.maxForce * 0.2; 
} 
else {
    // SCAN MODE
    // Turn in a circle, drifting towards the center
    action.desiredAngle = state.angle + 0.15;
    action.desiredForce = state.maxForce * 0.3;
    action.desiredLaunch = false;

    // Occasional center correction
    if (Math.random() < 0.05) action.desiredAngle = state.angleToCenter;
}
